using System.Collections; using System.Collections.Generic; using UnityEngine.SceneManagement; using UnityEngine; using System; using UnityEngine.UI;  public class createRetry : MonoBehaviour {     public GameObject m_retryPrefab;     public Transform m_contentRoot;      // Start is called before the first frame update     void Start()     {         Create();     }      // Update is called once per frame     public void Create()     {         if (SceneManager.GetActiveScene().name == "StageClear")         {             GameObject obj = (GameObject)Instantiate(m_retryPrefab, Vector3.zero, Quaternion.identity);             obj.transform.SetParent(m_contentRoot);             obj.transform.localScale = new Vector3(1, 1, 1);             obj.transform.localPosition= new Vector3(350, -350, 0);         }     } } 